package lab2;

public class DefaultValues {
	static byte aByte;
	static short aShort;
	static int anInt;
	static long aLong;
	static float aFloat;
	static double aDouble;
	static char aChar;
	static String aString;
	static boolean aBoolean;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println("Default value of a byte is " + aByte);
		System.out.println("Default value of a short is " + aShort);
		System.out.println("Default value of an int is " + anInt);
		System.out.println("Default value of a Long is " + aLong);
		System.out.println("Default value of a floate is " + aFloat);
		System.out.println("Default value of a double is " + aDouble);
		System.out.println("Default value of a char is " + aChar);
		System.out.println("Default value of a string is " + aString);
		System.out.println("Default value of a boolean is " + aBoolean);
	}

}
